        tokenType,
        openSea: parseOpenSeaMetadata(rawNft.contractMetadata?.openSea),
        contractDeployer: rawNft.contractMetadata?.contractDeployer,
        deployedBlockNumber: rawNft.contractMetadata?.deployedBlockNumber
