 * @public
 */
export type LogLevelString = 'debug' | 'info' | 'warn' | 'error' | 'silent';

const logLevelStringToEnum: { [key in LogLevelString]: LogLevel } = {
  debug: LogLevel.DEBUG,
  info: LogLevel.INFO,
  warn: LogLevel.WARN,
  error: LogLevel.ERROR,
  silent: LogLevel.SILENT
};

